mapper.select:
SELECT
FROM t_shop_home_base_info t LEFT JOIN t_bank_info b on t.legal_person_bank_card_l=b.bank_num LEFT JOIN t_community c ON t.community_code=c.community_code LEFT JOIN sys_salesman e ON t.ywq_code=e.`code` where t.del_flag=0
AND t.login_no=#{loginNo}
AND t.shop_home_code=#{shopHomeCode}

SELECT t3.* FROM ( select (@i :=@i + 1) AS item,t1.* from ( SELECT
FROM t_shop_home_base_info t LEFT JOIN t_bank_info b on t.legal_person_bank_card_l=b.bank_num LEFT JOIN t_community c ON t.community_code=c.community_code LEFT JOIN sys_salesman e ON t.ywq_code=e.`code` where t.del_flag=0 and t.apply_state=#{applyState} and t.apply_flag=#{applyFlag} and (t.apply_flag='N' OR t.apply_flag='Z') ) t1, (SELECT @i := 0) AS it ) t3
LIMIT #{key_offset},#{rows}
